// src/components/LibroList.js
import React, { useState, useEffect } from 'react';
import api from '../api';
import LibroItem from './LibroItem';

const LibroList = () => {
  const [libros, setLibros, onDeleteLibro] = useState([]);

  useEffect(() => {
    // Obtener libros del backend
    api.get('/')
      .then((response) => setLibros(response.data))
      .catch((error) => console.error('Error al obtener libros:', error));
  }, []);

  return (
    //<div>
      //<h2>Lista de Libros</h2>
      //{libros.map((libro) => (
        //<LibroItem key={libro._id} libro={libro} />
      //))}
    //</div>
    <div className="container mt-4">
      <h2 className="text-center mb-4">Lista de Libros</h2>
      {libros.length > 0 ? (
        <div className="row">
          {libros.map((libro) => (
            <div key={libro._id} className="col-md-6 col-lg-4 mb-4">
              <div className="card shadow-sm">
                <div className="card-body">
                  <h5 className="card-title">{libro.title}</h5>
                  <h6 className="card-subtitle mb-2 text-muted">{libro.author}</h6>
                  <p className="card-text">AÃ±o: {libro.publication_year}</p>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => onDeleteLibro(libro._id)}
                  >
                    Eliminar
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center">No hay libros disponibles.</p>
      )}
    </div>
  );
};

export default LibroList;
