// src/components/LibroForm.js
import React, { useState } from 'react';
import api from '../api';

const LibroForm = ({ onLibroAdded }) => {
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    genre: '',
    publication_year: '',
    status: 'Por leer',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    api.post('/', formData)
      .then((response) => {
        onLibroAdded(response.data);
        setFormData({ title: '', author: '', genre: '', publication_year: '', status: 'Por leer' });
      })
      .catch((error) => console.error('Error al agregar libro:', error));
  };

  return (
    
    <div className="container mt-4">
    <h2 className="text-center mb-4">Agregar un Nuevo Libro</h2>
    <form onSubmit={handleSubmit} className="bg-light p-4 rounded shadow-sm">
      <div className="mb-3">
        <label htmlFor="titulo" className="form-label">Título</label>
        <input
          type="text"
          id="titulo"
          className="form-control"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
          placeholder="Ingresa el título"
        />
      </div>
      <div className="mb-3">
        <label htmlFor="autor" className="form-label">Autor</label>
        <input
          type="text"
          id="autor"
          className="form-control"
          value={autor}
          onChange={(e) => setAutor(e.target.value)}
          placeholder="Ingresa el autor"
        />
      </div>
      <div className="mb-3">
        <label htmlFor="anio" className="form-label">Año</label>
        <input
          type="number"
          id="anio"
          className="form-control"
          value={anio}
          onChange={(e) => setAnio(e.target.value)}
          placeholder="Ingresa el año"
        />
      </div>
      <button type="submit" className="btn btn-primary w-100">Agregar Libro</button>
    </form>
  </div>
);
};


export default LibroForm;
