// src/components/LibroForm.js
import React, { useState } from 'react';
import api from '../api';

const LibroForm = ({ onLibroAdded }) => {
  const [formData, setFormData] = useState({
    titulo: '',
    autor: '',
    genero: '',
    anio_publicacion: '',
    estado: 'Por leer',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    api.post('/', formData)
      .then((response) => {
        onLibroAdded(response.data);
        setFormData({ titulo: '', autor: '', genero: '', anio_publicacion: '', estado: 'Por leer' });
      })
      .catch((error) => console.error('Error al agregar libro:', error));
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        name="titulo"
        placeholder="Título"
        value={formData.titulo}
        onChange={handleChange}
        required
      />
      <input
        name="autor"
        placeholder="Autor"
        value={formData.autor}
        onChange={handleChange}
        required
      />
      <input
        name="genero"
        placeholder="Género"
        value={formData.genero}
        onChange={handleChange}
      />
      <input
        name="anio_publicacion"
        type="number"
        placeholder="Año de publicación"
        value={formData.anio_publicacion}
        onChange={handleChange}
      />
      <select name="estado" value={formData.estado} onChange={handleChange}>
        <option value="Por leer">Por leer</option>
        <option value="Leyendo">Leyendo</option>
        <option value="Leído">Leído</option>
      </select>
      <button type="submit">Agregar Libro</button>
    </form>
  );
};

export default LibroForm;
